tapStdin() {
  var s;
  var ios = new StringInputStream(stdin);
  var lines = [];
  while ((s = ios.readLine()) !== null) {
    lines.add(s);
  }
  var args = new Options().arguments;
  var nRepeats = args.length > 0 ? Math.parseInt(args[0]) : 1;
  for (var i = 0; i < nRepeats; i++) {
    for (s in lines) {
      print(mask(s));
    }
  }
  exit(0);
}

main() {
