func tapStdin() {
  nRepeats := 1
  if len(os.Args) > 1 { nRepeats, _ = strconv.Atoi(os.Args[1]) }
  ios := bufio.NewReader(os.Stdin)
  if nRepeats > 1 {
    s, err := ios.ReadBytes('\n')
    lines := []string{}
    for err == nil {
      lines = append(lines, string(s))
      s, err = ios.ReadBytes('\n')
    }
    for i := 0; i < nRepeats; i++ {
      for _, s := range lines {
        fmt.Print(mask(s))
      }
    }
  } else {
    s, err := ios.ReadBytes('\n')
    for err == nil {
      fmt.Print(mask(string(s)))
      s, err = ios.ReadBytes('\n')
    }
  }
}

