#!/usr/bin/env ruby

def test_it s
  a = a.is_a?(String) ? s.scan(/\d/) : s
  a = a.map{|ns| ns.to_i }
  i = a.length - 2
  n = 0
  while i >= 0 do
    n = a[i] * 2
    if n == 10
      n = 1
    elsif n == 12
      n = 3
    elsif n == 14
      n = 5
    elsif n == 16
      n = 7
    elsif n == 18
      n = 9
    end
    a[i] = n
    i -= 2
  end
  total = 0
  a.each{|n| total += n }
  total % 10 == 0
end

def iterate s
  len = s.length
  result = false
  match_start = 0
  match_len = 0
  if len >= 14
    n = 0
    max_len = len > 16 ? 16 : len
    while n + max_len - 1 < len do
      result = test_it(s[n...n + max_len])
      if result
        match_start = n
        match_len = max_len
        break
      end
      if max_len == 16
        result = test_it(s[n...n + 15])
        if result
          match_start = n
          match_len = 15
          break
        end
      end
      if max_len == 16 or max_len == 15
        result = test_it(s[n...n + 14])
        if result
          match_start = n
          match_len = 14
          break
        end
      end
      n += 1
      max_len -= 1 if max_len > 14 and n + max_len >= len
    end
  end
  [result, match_start, match_len]
end

def mask s
  match_from = 0
  len = s.length
  masked_s = nil
  while match_from < len and md = s.match(/\d[\d\s\-]+\d/, match_from)
    found, match_start, match_len = iterate(md[0].scan(/\d/))
    if found
      masked_s = s[0..-1] if not masked_s
      n = md.begin(0)
      while match_start > 0
        match_start -= 1 if /\d/ =~ s[n]
        n += 1
      end
      match_from = n + 1
      while match_len > 0
        if /\d/ =~ s[n]
          masked_s[n] = 'X'
          match_len -= 1 
        end
        n += 1
      end
    else
      match_from = md.end(0)
    end
  end
  masked_s || s
end

